SELECT a.CBAA07,a.CBAA08,a.CBAA09,b.CBWA09,a.CBAA10,c.CBWA10,d.CALA08,e.CBSA08,f.CBAB15,e.CBSA01, g.CBPB10,g.CBPB12,g.CBPB08,g.CBPB15,f.CBAB09,f.CBAB11,f.CBAB13, h.CBPA08,l.CBAC09,m.CBLA09,a.CBAA01,g.CBPB01,f.CBAB01,f.CBAB08,n.CALA08 as pinpai from CBAA a LEFT JOIN CBWA b ON a.CBAA09=b.CBWA01 LEFT JOIN CBWA c on a.CBAA10=c.CBWA01 LEFT JOIN CALA d on d.CALA01=a.CBAA16 LEFT JOIN CBAB f on f.CBAA01=a.CBAA01 left join CBSA e ON e.CBSA01=f.CBAB14 LEFT JOIN CBPB g ON g.CBPB01=f.CBAB08 LEFT JOIN CBPA h ON h.CBPA01=g.CBPB14 LEFT JOIN CBAC l on l.CBAA01=a.CBAA01 LEFT JOIN CBLA m ON m.CBLA01=l.CBAC10 LEFT JOIN CALA n on n.CALA01=g.CBPB10 where
a.CBAA01 = #{cbaa01}
GROUP BY f.CBAB08
