mapper.select:
SELECT a.CBIB02,a.CBIB08, c.CBPB08 as goodsName,b.CBWA09 as winName,c.CBPB12, DATE_FORMAT(a.CBIB04,'%Y-%m') as yearAndMonth, a.CBIB10,a.CBIB11 ,a.CBIB12,a.CBIB13 , a.CBIB17 FROM CBIB a LEFT JOIN CBWA b on a.CBIB02=b.CBWA01 LEFT JOIN CBPB c ON c.CBPB01=a.CBIB08 where a.CBIB04 is not null
and c.CBPB12 like concat('%', #{cbpb12}, '%')
and c.CBPB12 in
#{cbwaItem}
and a.cbib02 = #{cbib02}
and DATE_FORMAT(a.CBIB04,'%Y-%m') in
#{monthly}
order by DATE_FORMAT(a.CBIB04,'%Y-%m') desc

SELECT a.CBIB19, ca.CBCA08 as customerName,DATE_FORMAT(a.CBIB04,'%Y-%m') as yearAndMonth, SUM(a.CBIB13) as outCount,SUM(a.CBIB14) as outMoney FROM CBIB a left join CBCA ca on ca.CBCA01 = a.CBIB19 WHERE a.CBIB05 = 5 and a.CBIB19 is not null GROUP BY a.CBIB19,DATE_FORMAT(a.CBIB04,'%Y-%m') HAVING 1=1
and a.cbib19 = #{customerId}
and yearAndMonth in
#{monthly}
order by DATE_FORMAT(a.CBIB04,'%Y-%m') desc

select ib.CBIB19,sum(ib.outCount) as outCountTotal,sum(ib.outMoney) as outMoneyTotal from (SELECT a.CBIB19, ca.CBCA08 as customerName,DATE_FORMAT(a.CBIB04,'%Y-%m') as yearAndMonth, SUM(a.CBIB13) as outCount,SUM(a.CBIB14) as outMoney FROM CBIB a left join CBCA ca on ca.CBCA01 = a.CBIB19 WHERE a.CBIB05 = 5 and a.CBIB19 is not null GROUP BY a.CBIB19,DATE_FORMAT(a.CBIB04,'%Y-%m') HAVING 1=1
and a.cbib19 = #{customerId}
and yearAndMonth in
#{monthly}
) as ib group by ib.CBIB19

SELECT cbib.* FROM `CBIB` cbib
LEFT JOIN CBPB pb ON pb.CBPB01 = cbib.CBIB08
where
cbib.CBIB01 !=0


  and cbib.CBIB02 in

    #{whIdItem}




  and cbib.CBIB08 in

    #{goodsItem}





  and pb.CBPB10 in

    #{brandItem}




      and cbib.CBIB04 >= #{startTime}


      and cbib.CBIB04 <=#{endTime}

order by cbib.CBIB04 desc
