mapper.select:
SELECT (@i:=@i+1) AS '序号' , e.CBPA07 as totalclassify, d.CBPA07,h.CALA08,c.CBPB12,c.CBPB15,c.CBPB08,b.CBIF09,f.CBWA09,g.lock_qty as lockQty,c.CBPB01 FROM CBIE a LEFT JOIN CBIF b on a.CBIE01=b.CBIE01 LEFT JOIN CBPB c on c.CBPB01=b.CBIF08 LEFT JOIN CBPA d on d.CBPA01=c.CBPB14 LEFT JOIN CBPA e ON e.CBPA01=d.CBPA09
LEFT JOIN CBWA f on f.CBWA01=a.CBIE09 LEFT JOIN gs_goods_use g ON b.CBIF08=g.goods_id and a.CBIE09=g.lock_qty LEFT JOIN CALA h ON c.CBPB10=h.CALA01, -- -- LEFT JOIN -- gs_goods_sku g -- ON -- a.CBIE09=g.wh_id, (SELECT @i:=0) AS itable where a.CBIE06=0
and f.CBWA09 in
#{cbwaItem}
and d.CBPA07 = #{cbpa07} and h.CALA08 = #{cala08} and c.CBPB08 = #{cbpb08} and c.CBPB01 = #{cbpb01}

SELECT ib.CBIB01,ib.CBIB02,ib.CBIB08,ib.CBIB15, pb.CBPB08, pb.CBPB15, pb.CBPB12,pb.CBPB10,pb.CBPB14,pb.CBPB01,
wa.CBWA09,max(ib.CBIB04) CBIB04
FROM CBIB ib
LEFT JOIN CBPB pb ON pb.CBPB01 = ib.CBIB08
LEFT JOIN CBWA wa ON wa.CBWA01 = ib.CBIB02
GROUP BY ib.CBIB08,ib.CBIB02
HAVING 1=1

  and wa.CBWA09 in

    #{cbwaItem}


 and pb.CBPB14 = #{cbpa07} 
 and pb.CBPB10 = #{cbpb10} 
 and pb.CBPB08 = #{cbpb08}
 and pb.CBPB01 = #{cbpb01}

SELECT ib.CBIB01, ib.CBIB02, ib.CBIB08, ib.CBIB15, pb.CBPB08, pb.CBPB15, pb.CBPB12, pb.CBPB10, pb.CBPB14, pb.CBPB01, wa.CBWA09 FROM CBIB ib LEFT JOIN CBPB pb ON pb.CBPB01 = ib.CBIB08 LEFT JOIN CBWA wa ON wa.CBWA01 = ib.CBIB02 WHERE ib.CBIB01 IN ( SELECT max( tb.CBIB01 ) FROM CBIB tb GROUP BY tb.CBIB08, tb.CBIB02 )
and wa.CBWA09 in
#{cbwaItem}
and pb.CBPB10 = #{cbpb10} and pb.CBPB01 = #{cbpb01}
order by ib.CBIB04 desc

SELECT sn.id,c.CBWA09,d.CBLA09,f.CBPA07,h.CALA08,e.CBPB12,e.CBPB08,e.CBPB15,sn.sn,sn.in_time as inTime, sn.status,sn.groud_status as groudStatus,sn.repair_status as repairStatus,sn.goods_id as goodsId,sn.location_id as locationId FROM gs_goods_sn sn
LEFT JOIN CBWA c ON sn.wh_id=c.CBWA01 LEFT JOIN CBLA d ON d.CBLA01=sn.location_id LEFT JOIN CBPB e ON e.CBPB01=sn.goods_id LEFT JOIN CBPA f ON f.CBPA01=e.CBPB14 LEFT JOIN CBPA g ON f.CBPA09=g.CBPA01 LEFT JOIN CALA h ON h.CALA01=e.CBPB10 LEFT JOIN sys_user u ON u.user_id = sn.create_by WHERE (sn.delete_flag =0 or sn.delete_flag is null) and sn.status!=3
and c.CBWA09 in
#{cbwaItem}
and d.CBLA09 in
#{cblaItem}
and sn.goods_id =#{cbig09}
and sn.sn like concat('%', #{cbig10}, '%')
and e.cbpb01 =#{cbpb01}
and e.cbpb10 =#{cbpb10}
and sn.status =#{status}
and sn.groud_status =#{groudStatus}
${params.dataScope} order by sn.create_time desc

SELECT sn.id,c.CBWA09,d.CBLA09,f.CBPA07,h.CALA08,e.CBPB12,e.CBPB08,e.CBPB15,sn.sn,sn.in_time as inTime,sn.out_time as outTime, sn.status,sn.groud_status as groudStatus,sn.repair_status as repairStatus,sn.goods_id as goodsId,sn.location_id as locationId FROM gs_goods_sn sn
LEFT JOIN CBWA c ON sn.wh_id=c.CBWA01 LEFT JOIN CBLA d ON d.CBLA01=sn.location_id LEFT JOIN CBPB e ON e.CBPB01=sn.goods_id LEFT JOIN CBPA f ON f.CBPA01=e.CBPB14 LEFT JOIN CBPA g ON f.CBPA09=g.CBPA01 LEFT JOIN CALA h ON h.CALA01=e.CBPB10 LEFT JOIN sys_user u ON u.user_id = sn.create_by WHERE (sn.delete_flag =0 or sn.delete_flag is null) and sn.status=3
and c.CBWA09 in
#{cbwaItem}
and d.CBLA09 in
#{cblaItem}
and sn.goods_id =#{cbig09}
and sn.sn like concat('%', #{cbig10}, '%')
and e.cbpb01 =#{cbpb01}
and e.cbpb10 =#{cbpb10}
and sn.status =#{status}
and sn.groud_status =#{groudStatus}
and sn.out_time >= #{startTime}
and sn.out_time <=#{endTime}
${params.dataScope} order by sn.create_time desc

SELECT b.CBWA09,a.CBIB04,a.CBIB03,a.CBIB17,a.CBIB06,c.CBPB08,c.CBPB12, a.CBIB09,a.CBIB10,a.CBIB11,b.CBWA11,a.CBIB12,a.CBIB13,a.CBIB14,a.CBIB15,a.CBIB16 FROM CBIB a LEFT JOIN CBWA b on a.CBIB02=b.CBWA01 LEFT JOIN CBPB c ON c.CBPB01=a.CBIB08 WHERE b.CBWA06=0
AND a.CBIB17 like concat('%', #{cbib17}, '%')
and b.CBWA01 = #{cbwa01}
and a.CBIB08 = #{cbpb01}
and a.cbib04 >= #{startTime}
and a.cbib04 <=#{endTime}
order by a.CBIB04 desc
# LIMIT 100

SELECT a.CBOA07,a.CBOA08,c.CBCA08,d.nick_name as caua17,f.CALA08,g.CBPA08,e.CBPB12,b.CBOB10,b.CBOB09, (b.CBOB09-b.CBOB10)as num,b.CBOB11,b.CBOB12 FROM CBOA a LEFT JOIN CBOB b ON a.CBOA01=b.CBOA01 LEFT JOIN CBCA c ON c.CBCA01=a.CBOA09 LEFT JOIN sys_user d ON d.user_id=a.CBOA10 LEFT JOIN CBPB e ON e.CBPB01=b.CBOB08 LEFT JOIN CALA f ON f.CALA01=e.CBPB10 LEFT JOIN CBPA g ON g.CBPA01=e.CBPB14 where a.CBOA06=0
and c.CBCA08 = #{cbca08}
and d.user_id =#{caua17}
and e.cbpb01=#{cbpb01}
and a.CBOA08 >= #{startTime}
and a.CBOA08 <=#{endTime}
and a.CBOA07 like concat('%', #{cboa07}, '%')
order by a.CBOA02 desc

SELECT e.CBPA07,d.CBPA08,f.CALA08,c.CBPB12,c.CBPB15,c.CBPB08,b.CBOF09,g.CBCA08,a.CBOE08 from CBOE a LEFT JOIN CBOF b ON a.CBOE01=b.CBOE01 LEFT JOIN CBPB c ON b.CBOF08=c.CBPB01 LEFT JOIN CBPA d ON d.CBPA01=c.CBPB14 LEFT JOIN CBPA e ON e.CBPA01=d.CBPA09 LEFT JOIN CALA f ON f.CALA01=c.CBPB10 LEFT JOIN CBCA g ON g.CBCA01=a.CBOE09 where a.CBOE06=0
and d.CBPA08 like concat('%', #{cbpa08}, '%')
and e.CBPA07=#{cbpa07}
and f.CALA08 = #{cala08}
and c.cbpb01=#{cbpb01}
order by b.CBOF02 desc
